draw((0,0)--(12,0)--(12,20)--(0,20)--(0,0)--(12,20));
draw((0,14)--(12,6));
label("$A$",(0,20),W);
label("$Z$",(0,14),W);
label("$D$",(0,0),W);
label("$Q$",(6,10),2S);
label("$B$",(12,20),E);
label("$W$",(12,6),E);
label("$C$",(12,0),E);
[/asy]
