[asy]
draw((0,0)--(-10,0)--(-10,10)--(0,10)--cycle);
draw((0,10)--(0,20)--(-30,20)--(-30,10)--cycle);
draw((-10,10)--(-10,20));
draw((-20,10)--(-20,20));
draw((-20,20)--(-20,30)--(-40,30)--(-40,20)--cycle);
